import React, { useState } from 'react';
import Header from './Header';
import '../styles/style.css';

const QuestionListPage: React.FC = () => {
  const [portfolio, setPortfolio] = useState<string>('저는 1녀1남의 막내로 태어나 화목한 가정에서...');
  const [answers, setAnswers] = useState<{ [key: number]: string }>({});
  const [darkMode, setDarkMode] = useState(false);

  const handleAnswerChange = (questionId: number) => (event: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setAnswers({
      ...answers,
      [questionId]: event.target.value,
    });
  };

  const questions = [
    '당신은 막내로서 어떤 역할을 주로 수행했나요',
    '화목한 가정은 어떻게 만들어질까요',
    '집에서 어떤 심부름 해보셨어요',
    '막내는 사랑을 많이 받나요',
  ];

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (

    <div className={darkMode ? 'dark-mode' : ''}>
      <Header toggleDarkMode={toggleDarkMode} />
      <div className="content">
        <h1>포트폴리오를 올려봐요</h1>
        <p>포트폴리오 정보를 입력하고 맞춤형 질문을 받아보세요</p>
        <div className='input-back-box'>
          <textarea placeholder="Enter your portfolio details here..."></textarea>
          <button className="submit-button" onClick={handleButtonClick}>업로드</button>

        </div>
      </div>
      <div className="container">
        <div className="portfolio">
          <h2>나의 포트폴리오</h2>
          <p>{portfolio}</p>
        </div>
        <div className="questions">
          <h2>예상 면접 질문</h2>
          {questions.map((question, index) => (
            <div key={index} className="question">
              <h3>{index + 1}. {question}</h3>
              <textarea onChange={handleAnswerChange(index)} value={answers[index] || ''} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default QuestionListPage;
