import React from 'react';
import styled from 'styled-components';

const Container = styled.div`
  padding: 20px;
  text-align: center;
  max-width: 1200px;
  margin: auto;
`;

const FileDropArea = styled.div`
  width: 100%;
  padding: 20px;
  margin-top: 20px;
  border: 2px dashed ${({ theme }) => theme.border};
  border-radius: 5px;
  text-align: center;
  cursor: pointer;
`;

const Button = styled.button`
  padding: 10px 20px;
  margin-top: 20px;
  background-color: ${({ theme }) => theme.buttonBackground};
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: ${({ theme }) => theme.buttonHoverBackground};
  }
`;

const PortfolioUploadPage: React.FC = () => {
  return (
    <Container>
      <h1>포트폴리오를 올려봐요</h1>
      <p>포트폴리오를 분석해서 면접을 도와드릴게요</p>
      <p>당신의 잠재력을 일깨워드릴게요</p>
      <FileDropArea>
        Drag and drop your portfolio file here or <Button as="span">Upload</Button>
      </FileDropArea>
    </Container>
  );
};

export default PortfolioUploadPage;
