import React, { useState } from 'react';
import './App.css';

const PortfolioUploadPagePDF: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [darkMode, setDarkMode] = useState(false);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files) {
      setFile(event.target.files[0]);
    }
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={darkMode ? 'dark-mode' : ''}>
      <header>
        <h1>Questio</h1>
        <button onClick={toggleDarkMode}>
          {darkMode ? '라이트 모드' : '다크 모드'}
        </button>
      </header>
      <div className="container">
        <h2>포트폴리오를 올려봐요</h2>
        <p>포트폴리오를 분석해서 면접을 도와드릴게요 당신의 잠재력을 일깨워드릴게요</p>
        <div className="upload-box">
          <input type="file" id="file" accept=".pdf" onChange={handleFileChange} />
          <label htmlFor="file">포트폴리오를 여기에 드래그 해주세요</label>
          {file && <p>{file.name}</p>}
          <button>업로드</button>
        </div>
      </div>
    </div>
  );
};

export default PortfolioUploadPagePDF;
